import { useReducer } from "react";
import CartContext from "./Cart-Context";

function cartReducer(action, state){
    if(action.type === 'ADD'){
        const updatedItems = state.item.concat();
        const newTotalAmount = state.totalAmount  + action.item.price * action.item.amount;
        return{
            items:updatedItems, totalAmount:newTotalAmount
        }
    }
    return{defaultCartState}
}

function CartProvider(props){
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);
    const AddCartItem = (item)=>{
        dispatchCartAction({type:'ADD',item:item})
    }
    const RemoveCartItem = (id) =>{
        dispatchCartAction({type:'REMOVE',id:id})
    }
    const CartItemProvider = {

        items:[],
        TotalAmount:0,
        addItem:AddCartItem,
        removeItem:RemoveCartItem
    }



    return <CartContext.Provider value={CartItemProvider}>{props.children}</CartContext.Provider>
}

export default CartProvider;
