import { useReducer } from "react";
import CartContext from "./Cart-Context";

const defaultCartState = {items:[],totalAmount:0}
const cartReducer = (state, action) =>{
    if(action.type === 'ADD'){

        const newTotalAmount = state.totalAmount  + action.item.price * action.item.amount;

        const existCartItemIndex = state.items.findIndex(
            (item) => item.id === action.id
        );
        const existCartItem = state.items[existCartItemIndex];
        let updatedItems;
        if (existCartItem){
            const updatedItem ={
                ...existCartItem, amount:existCartItem.amount + action.item.amount,
            }
            updatedItems = [...state.items];
            updatedItems[existCartItemIndex] = updatedItem;
        }
        else{
            updatedItems = state.items.concat(action.item);
        }

        return{
            items: updatedItems,
            totalAmount: newTotalAmount
        };
    }
    if(action.type ==='REMOVE'){
        const existingCartItemIndex = state.items.findIndex(
            (item)=> item.id=== action.id
        );
        console.log("working");
        const existingItem = state.items[existingCartItemIndex];
        const newTotalAmount =state.totalAmount - existingItem.price;
        let updatedItems;
        if(existingItem.amount ===1){
            console.log("working if");
            updatedItems = state.items.filter(item => item.id === action.id)
        }
        else{
            console.log("working else");
            const updatedItem= {...existingItem, amount:existingItem.amount -1}
            updatedItems = [...state.items];
            updatedItems[existingCartItemIndex] = updatedItem;
        }
        return{
            items: updatedItems,
            totalAmount : newTotalAmount,
        }
    }
    return defaultCartState;
}

const CartProvider = props =>{

    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);
    const AddCartItem = (item)=>{
        dispatchCartAction({type:'ADD',item:item});
    };
    const RemoveCartItem = (id) =>{
        dispatchCartAction({type:'REMOVE',id:id});
    };

    const CartItemProvider = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: AddCartItem,
        removeItem: RemoveCartItem
    }



    return( <CartContext.Provider value={CartItemProvider}>{props.children}</CartContext.Provider>);
};

export default CartProvider;
