import classes from "./MealForm.module.css";
import Input from "../../UI/Input";

function MealForm(props){
    return<form className={classes.form}>
        <label>{props.label}</label>
        <button>+ Add</button>
        <Input 
            label="Amount"
            input={{
                id:'amount_'+props.id,
                max:5,
                min:1,
                type:"number",
                step:1,
                defaultValue:1
            }}
            />

    </form>
}

export default MealForm;
